%                       ---BUILDING THE LIST---
%  Forms a list of all possible quadruples in the form of [X,Y,SUM,PRODUCT]
%            where X is less than Y and X+Y is less than 100
% ------------------------------------------------------------------------------

%Builds a list of numbers from 2 to 100
make_list(X,X,[X]):- !.
make_list(First, Last, [First|List]) :- First =< Last,
                                        Next is First + 1,
                                        make_list(Next, Last, List).

%Takes 2 lists and forms quadruples in the form of [X,Y,SUM,PRODUCT] of two elements, one from each list
product_and_sum([],_,[]) :- !.
product_and_sum([Head|Tail], Y, List) :- make_quads(Head, Y, Quads),
                                         product_and_sum(Tail, Y, Row),
                                         append(Quads, Row, List), !.

%For a given number construct a quadruple in the form of [X,Y,SUM,PRODUCT] 
make_quads(_,[],[]) :- !.
make_quads(X, [Y|Tail], List):- (X =< Y) ; (X + Y > 100),  !, 
                                List = L,
                                make_quads(X, Tail, L).
make_quads(X, [Y|Tail], List) :- Sum is X + Y,
                                 Product is X * Y,
                                 List = [[Y, X, Sum, Product]|L],
                                 make_quads(X, Tail, L).

%app combines 2 lists to form a single list
app([],L,L).
app([H|T],A,[H|B]) :- app(T,A,B).
 
%                               ---MERGE SORT FUNCTION---
%     Divide a list into two spereate lists. Recursively call divide to sort out the 
%                 two subs lists and them merge them back together
% ------------------------------------------------------------------------------
 
mergesort([], []).  
mergesort([X], [X]).
mergesort([X,Y|Rest], Sorted) :- divide([X,Y|Rest],List1,List2),
                                 mergesort(List1, Sorted1),
                                 mergesort(List2, Sorted2),
                                 merge_lists(Sorted1, Sorted2, Sorted), !.

%Takes in a list and divides it into 2 new lists
divide([],[],[]).
divide([X],[X],[]).
divide([X,Y|Rest],[X|Rx],[Y|Ry]) :- divide(Rest,Rx,Ry).

%Takes in 2 lists and merges them into 1 list
%The lists are merged so that they are in product order
merge_lists(X,[],X).
merge_lists([],Y,Y).
merge_lists([X|Rx],[Y|Ry],[X|M]) :- nth0(3, X, A), %Get the product of the first list
                                    nth0(3, Y, B), %Get the product of the second list
                                    A =< B, 
                                    merge_lists(Rx,[Y|Ry],M).
merge_lists([X|Rx],[Y|Ry],[Y|M]) :- nth0(3, X, A),  %Get the product of the first list
                                    nth0(3, Y, B),  %Get the product of the second list
                                    A > B,  
                                    merge_lists([X|Rx],Ry,M).
 
 
%                   ---REMOVE ITEMS FUNCTIONS---
% Removes specific quadruples from the list of quadruples depending on the
%                    SUM or PRODUCT specificed
% ------------------------------------------------------------------------------

%Takes in a list of quadruples, remove specific quadruples and returns the remaining list of quadruples
remove(X, N, Res) :- remove(X, N, [], Res).
remove([], _, _, []) :- !. %Passing in an empty list will return an empty list

%If N = 1 remove all quadruples which have duplicate SUMS
remove([H|T], N, Sort, Res) :- nth0(2, H, A),               %Get the SUM from the first quadruple
                               (N == 1; N == 5) , 
                               is_in_previous(A, Sort), !,  %Check to see if it is in the sorted list
                               remove(T, N, Sort, Res).
remove([H|T], N, Sort, Res) :- nth0(2, H, A),               %Get the SUM from the first quadruple
                               N == 1, 
                               check_dup_sum(A, T), !,      %Check to see if it is in the rest of the list
                               remove(T, N, [A|Sort], Res).
remove([H|T], N, Sort, Res) :- N == 1, !,                   %If no duplicates can be found move onto the next SUM
                               Res = [H|NRes],
                               remove(T, N, Sort, NRes).

%If N = 2 remove all quadruples which have unique PRODUCTS
remove([H|T], N, Sort, Res) :- nth0(3, H, A),               %Get the PRODUCT from the first quadruple
                               N == 2, 
                               is_in_previous(A, Sort), !,  %Check to see if it is in the sorted list
                               Res = [H|NRes],
                               remove(T, N, Sort, NRes).
remove([H|T], N, Sort, Res) :- nth0(3, H, A),               %Get the PRODUCT from the first quadruple
                               N == 2, 
                               check_dup_product(A, T), !,  %Check to see if it is in the rest of the list
                               Res = [H|NRes],
                               remove(T, N, [A|Sort], NRes).
remove([H|T], N, Sort, Res) :- N == 2, !,                   %If no duplicates can be found the result stays the same
                               remove(T, N, Sort, Res).

%If N = 3 returns a list oF SUMS which have unique PRODUCTS
remove([H|T], N, Sort, Res) :- nth0(3, H, A),               %Get the PRODUCT from the first quadruple
                               N == 3, 
                               is_in_previous(A, Sort), !,  %Check to see if it is in the sorted list
                               remove(T, N, Sort, Res).
remove([H|T], N, Sort, Res) :- nth0(3, H, A),               %Get the PRODUCT from the first quadruple
                               N == 3, 
                               check_dup_product(A, T), !,  %Check to see if it is in the rest of the list
                               remove(T, N, [A|Sort], Res).
remove([H|T], N, Sort, Res) :- N == 3, !,                   %If no duplicates can be found return the SUM in a list
                               nth0(2, H, A), 
                               Res = [A|NRes],
                               remove(T, N, Sort, NRes).

%If N = 4 remove all quadruples with duplicate PRODUCTS
remove([H|T], N, Sort, Res) :- nth0(3, H, A),               %Get the PRODUCT from the first quadruple
                               N == 4, 
                               is_in_previous(A, Sort), !,  %Check to see if it is in the sorted list
                               remove(T, N, Sort, Res).
remove([H|T], N, Sort, Res) :- nth0(3, H, A),               %Get the PRODUCT from the first quadruple
                               N == 4, 
                               check_dup_product(A, T), !,  %Check to see if it is in the rest of the list
                               remove(T, N, [A|Sort], Res).
remove([H|T], N, Sort, Res) :- N == 4, !,                   %If no duplicates can be found update the lisT
                               Res = [H|NRes],
                               remove(T, N, Sort, NRes).

%If N = 5 take a list of SUMS and remove all quadruples with that SUMS
remove([H|T], N, Y, [H|Res]) :- N == 5, !,
                                remove(T, N, Y, Res).

%Checks to see if the value of X is a memeber of the passed list and returns true if it is
is_in_previous(X, [Y|T]) :- X = Y, !; 
                            is_in_previous(X, T).

%Checks to see if the value of X can be found elsewhere in the list and returns true if it can
check_dup_product(X, [H|T]) :- nth0(3, H, A), %Gives the 4th element from H, which is a list, and binds it to A
                               X = A, !;
                               check_dup_product(X, T).

%Checks to see if the value of X can be found elsewhere in the list and returns true if it can
check_dup_sum(X, [H|T]) :- nth0(2, H, A), %Gives the 3rd element from H, which is a list, and binds it to A
                           X = A, !;
                           check_dup_sum(X, T).

%                           ---STATEMENTS--- 
% ------------------------------------------------------------------------------

s1(Q, N):- make_list(2, N, X),
           product_and_sum(X, X, Y),
           mergesort(Y, Z),
           remove(Z, 2, Q),!.
 
s2(Q, N) :- s1(S1Result, N),
            make_list(2, N, X), 
            product_and_sum(X, X, Y),
            mergesort(Y, Sorted),
            remove(Sorted, 3, Unique_Products),
            remove(S1Result, 5, Unique_Products, Q), !.
 
s3(Q, N) :- s2(S2Result, N),
            remove(S2Result, 4, Q), !.
 
s4(Q, N) :- s3(S3Result, N),
            remove(S3Result, 1, Q), !.




